-- ==================================================================================================================================================================================-- Versión:				<1.0>-- Creado por:			<Jose Gabriel Cordero Soto>-- Fecha de creación:	<13/04/2021>-- Descripción:			<Permite agregar un registro en la tabla: PropuestaDocumento.>-- ==================================================================================================================================================================================CREATE PROCEDURE	[DefensaPublica].[PA_AgregarPropuestaDocumento]	@CodPropuesta				UNIQUEIDENTIFIER,	@CodArchivo					UNIQUEIDENTIFIER,	@CodRepresentacion			UNIQUEIDENTIFIER,	@CodPuestoTrabajo			VARCHAR(14),	@EstadoPropuesta			CHAR(1),	@FechaPropuesta				DATETIME2(7)	ASBEGIN	--Variables	DECLARE	@L_TU_CodPropuesta			UNIQUEIDENTIFIER		= @CodPropuesta,			@L_TU_CodArchivo			UNIQUEIDENTIFIER		= @CodArchivo,			@L_TU_CodRepresentacion		UNIQUEIDENTIFIER		= @CodRepresentacion,			@L_TC_CodPuestoTrabajo		VARCHAR(14)				= @CodPuestoTrabajo,			@L_TC_EstadoPropuesta		CHAR(1)					= @EstadoPropuesta,			@L_TF_FechaPropuesta		DATETIME2(7)			= @FechaPropuesta							--Cuerpo	INSERT INTO	DefensaPublica.PropuestaDocumento	WITH (ROWLOCK)	(		TU_CodPropuesta,				TU_CodArchivo,					TU_CodRepresentacion,			TC_CodPuestoTrabajo,					TC_EstadoPropuesta,				TF_FechaPropuesta							)	VALUES	(		@L_TU_CodPropuesta,				@L_TU_CodArchivo,				@L_TU_CodRepresentacion,			@L_TC_CodPuestoTrabajo,					@L_TC_EstadoPropuesta,			@L_TF_FechaPropuesta			)END
GO
