-- ==================================================================================================================================================================================-- Versión:				<1.0>-- Creado por:			<Jose Gabriel Cordero Soto>-- Fecha de creación:	<24/05/2021>-- Descripción:			<Permite agregar un registro en la tabla: ObservacionExpedienteLegajo.>-- ==================================================================================================================================================================================CREATE PROCEDURE	[Expediente].[PA_AgregarObservacionExpedienteLegajo]	@CodObservacionExpedienteLegajo		UNIQUEIDENTIFIER,	@NumeroExpediente			CHAR(14),	@CodLegajo					UNIQUEIDENTIFIER	= NULL,	@UsuarioRed					VARCHAR(30),	@Observacion				VARCHAR(255),	@FechaCreacion				DATETIME2(7),	@Urgente					BIT,	@CodEstado					TINYINTASBEGIN	--Variables	DECLARE	@L_TU_CodObservacionExpedienteLegajo		UNIQUEIDENTIFIER	= @CodObservacionExpedienteLegajo,			@L_TC_NumeroExpediente						CHAR(14)			= @NumeroExpediente,			@L_TU_CodLegajo								UNIQUEIDENTIFIER	= @CodLegajo,			@L_TC_UsuarioRed							VARCHAR(30)			= @UsuarioRed,			@L_TC_Observacion							VARCHAR(255)		= @Observacion,			@L_TF_FechaCreacion							DATETIME2(7)		= @FechaCreacion,			@L_TB_Urgente								BIT					= @Urgente,			@L_TN_CodEstado								TINYINT				= @CodEstado				--Cuerpo	INSERT INTO	Expediente.ObservacionExpedienteLegajo	WITH (ROWLOCK)	(		TU_CodObservacionExpedienteLegajo,				TC_NumeroExpediente,			TU_CodLegajo,					TC_UsuarioRed,							TC_Observacion,									TF_FechaCreacion,				TB_Urgente,						TN_CodEstado	)	VALUES	(		@L_TU_CodObservacionExpedienteLegajo,			@L_TC_NumeroExpediente,			@L_TU_CodLegajo,				@L_TC_UsuarioRed,						@L_TC_Observacion,								@L_TF_FechaCreacion,			@L_TB_Urgente,					@L_TN_CodEstado			)END
GO
