-- ==================================================================================================================================================================================-- Versión:				<1.0>-- Creado por:			<Aida Elena Siles Rojas>-- Fecha de creación:	<30/10/2020>-- Descripción:			<Permite agregar un registro en la tabla: ResultadoRecursosArchivos.>-- ==================================================================================================================================================================================CREATE PROCEDURE	[Expediente].[PA_AgregarResultadoRecursosArchivos]	@CodResultadoRecurso		UNIQUEIDENTIFIER,	@CodArchivo					UNIQUEIDENTIFIER,	@NumeroExpediente			CHAR(14)ASBEGIN	--Variables	DECLARE	@L_TU_CodResultadoRecurso		UNIQUEIDENTIFIER		= @CodResultadoRecurso,			@L_TU_CodArchivo				UNIQUEIDENTIFIER		= @CodArchivo,			@L_TC_NumeroExpediente			CHAR(14)				= @NumeroExpediente	--Cuerpo	INSERT INTO	Expediente.ResultadoRecursosArchivos	WITH (ROWLOCK)	(		TU_CodResultadoRecurso,			TU_CodArchivo,			TC_NumeroExpediente					)	VALUES	(		@L_TU_CodResultadoRecurso,		@L_TU_CodArchivo,		@L_TC_NumeroExpediente					)END
GO
