-- ==================================================================================================================================================================================-- Versión:				<1.0>-- Creado por:			<Aida Elena Siles Rojas>-- Fecha de creación:	<05/11/2020>-- Descripción:			<Permite agregar un registro en la tabla: ResultadoSolicitudArchivos.>-- ==================================================================================================================================================================================CREATE PROCEDURE	[Expediente].[PA_AgregarResultadoSolicitudArchivos]	@CodResultadoSolicitud		UNIQUEIDENTIFIER,	@CodArchivo					UNIQUEIDENTIFIER,	@NumeroExpediente			CHAR(14)ASBEGIN	--Variables	DECLARE	@L_TU_CodResultadoSolicitud		UNIQUEIDENTIFIER	= @CodResultadoSolicitud,			@L_TU_CodArchivo				UNIQUEIDENTIFIER	= @CodArchivo,			@L_TC_NumeroExpediente			CHAR(14)			= @NumeroExpediente	--Cuerpo	INSERT INTO	Expediente.ResultadoSolicitudArchivos	WITH (ROWLOCK)	(		TU_CodResultadoSolicitud,			TU_CodArchivo,					TC_NumeroExpediente					)	VALUES	(		@L_TU_CodResultadoSolicitud,		@L_TU_CodArchivo,				@L_TC_NumeroExpediente				)END
GO
