-- ==================================================================================================================================================================================-- Versión:				<1.0>-- Creado por:			<Karol Jiménez Sánchez>-- Fecha de creación:	<20/10/2020>-- Descripción:			<Permite agregar un registro en la tabla: InventarioDcom.>-- ==================================================================================================================================================================================CREATE PROCEDURE [Migracion].[PA_AgregarInventariadoDcom]	@IDACO				INT,	@CARPETA			VARCHAR(14),	@CodContexto		VARCHAR(4),	@NumeroExpediente	VARCHAR(14),	@Hash				VARCHAR(100)		= NULL,	@CodArchivo			UNIQUEIDENTIFIER	= NULL,	@Estado				CHAR, 	@FechaInventariado	DATETIME2(3)		= NULLASBEGIN	--Variables.DECLARE  @L_IDACO					INT					= @IDACO,		 @L_CARPETA					VARCHAR(14)			= @CARPETA,		 @L_TC_CodContexto			VARCHAR(4)			= @CodContexto,		 @L_TC_NumeroExpediente		VARCHAR(14)			= @NumeroExpediente,		 @L_TC_Hash					VARCHAR(100)		= @Hash,		 @L_TU_CodArchivo			UNIQUEIDENTIFIER	= @CodArchivo,		 @L_TC_Estado				CHAR				= @Estado, 		 @L_TF_FechaInventariado	DATETIME2(3)		= @FechaInventariado	--Lógica.	INSERT INTO	Migracion.InventariadoDcom WITH(ROWLOCK)	(		IDACO,			CARPETA,			TC_CodContexto,		TC_NumeroExpediente,		TC_Hash,		TU_CodArchivo,		TC_Estado,			TF_FechaInventariado	)	VALUES	(		@L_IDACO,		@L_CARPETA,			@L_TC_CodContexto,	@L_TC_NumeroExpediente,		@L_TC_Hash,		@L_TU_CodArchivo,	@L_TC_Estado,		@L_TF_FechaInventariado	)END
GO
